package com.collar.var;

public class Varriable_04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int intDan = 7;
		int intNum = 1;
		
		
		System.out.println("====================");
		System.out.printf("%d 구구단\n", intDan);
		System.out.println("--------------------");
		System.out.printf("%d x %d = %d\n",
							intDan,
							intNum,
							intDan*intNum);
		intNum ++;
		
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;
		System.out.printf("%d x %d = %d\n",
				intDan,
				intNum,
				intDan*intNum);
		intNum ++;

	}

}
